draw((1,0)--(1,3),linewidth(1));
draw((2,0)--(2,3),linewidth(1));
draw((0,1)--(3,1),linewidth(1));
draw((0,2)--(3,2),linewidth(1));
label("8",(.5,2.5));
label("1",(1.5,2.5));
label("6",(2.5,2.5));
label("3",(.5,1.5));
label("5",(1.5,1.5));
label("7",(2.5,1.5));
label("4",(.5,.5));
label("9",(1.5,.5));
label("2",(2.5,.5));
[/asy]

