draw(pt(4)--ptp(3)--pt(2), gray(0.6) + linetype("4 4"));
draw(ptp(4)--ptp(3)--ptp(2), gray(0.6) + linetype("4 4"));

// draw cube
for(int i = 0; i < 4; ++i){
draw(P[1]--P[i]); draw(Pp[1]--Pp[i]);
for(int j = 0; j < 4; ++j){
if(i == 1 || j == 1 || i == j) continue;
draw(P[i]--Pp[j]); draw(Pp[i]--P[j]);
}
dot(P[i]); dot(Pp[i]);
dot(pt(i)); dot(ptp(i));
}

