}

draw(arch);
t = v*Sin(theta)/g;
dot((v*t*Cos(theta),v*t*Sin(theta) - 1/2*g*t^2),red);

theta = 40;
arch = (0,0);

for (t = 0; t <= 2*v*Sin(theta)/g; t = t + 0.01) {
  arch = arch--(v*t*Cos(theta),v*t*Sin(theta) - 1/2*g*t^2);
}

draw(arch);
t = v*Sin(theta)/g;
dot((v*t*Cos(theta),v*t*Sin(theta) - 1/2*g*t^2),red);

theta = 110;
arch = (0,0);

for (t = 0; t <= 2*v*Sin(theta)/g; t = t + 0.01) {
  arch = arch--(v*t*Cos(theta),v*t*Sin(theta) - 1/2*g*t^2);
}

