unitsize(0.4 cm);

path parab = (16,-8);
real y;
pair A, B, O;
real a, b, r;

a = (10 + 2*sqrt(5))/5;
b = (10 - 2*sqrt(5))/5;
A = (a^2,2*a);
B = (b^2,2*b);
O = (A + B)/2;
r = a + b;

for (y = -8; y <= 8; y = y + 0.2) {
  parab = parab--(y^2/4,y);
}

