real y;
pair A, B, O;
real a, b, r;

a = (10 + 2*sqrt(5))/5;
b = (10 - 2*sqrt(5))/5;
A = (a^2,2*a);
B = (b^2,2*b);
O = (A + B)/2;
r = a + b;

for (y = -8; y <= 8; y = y + 0.2) {
  parab = parab--(y^2/4,y);
}

draw(parab,red);
draw((-2,0)--(16,0));
draw((0,-8)--(0,8));
draw(Circle(O,r));
draw(A--B);
draw(O--(O.x,0),dashed);

